import React, {useState} from 'react'
import './GameOver.css'
import database from '../firebase'
import IntroPage from './IntroPage'

function GameOver(props) {
    database.ref('/users/times').push(props.time)
    let data = database.ref('/users/times')
    let bestTime = props.time
    let newRecord = false

    data.once("value").then((snapshot) => {
        snapshot.forEach((childSnapshot) => {
            if (childSnapshot.val() < bestTime)
                bestTime = childSnapshot.val()
        })
    })
    if (bestTime == props.time) newRecord = true

    return(
        <div id= "background">
            <div className= "section">
                <p style= {{fontSize: '6vmin', marginTop: 20, marginLeft: 20, marginRight: 20}}>You Passed!</p>
                <p style= {{fontSize: '4vmin', marginTop: 20, marginLeft: 20, marginRight: 20}}>Time: {props.time}</p>
                {newRecord ? <p>New Record!!</p>:<p>Best Time: {bestTime}</p>}
            </div>
            <button className = "new_game" onClick= {() => <IntroPage/>}>New Game</button>
        </div>
    )
}

export default GameOver
