import React, {useState} from 'react'
import './GameOver.css'
import database from '../firebase'
import IntroPage from './IntroPage'

function GameOver(props) {
    database.ref('/users/'+props.difficulty).push(props.time)
    let data = database.ref('/users/'+props.difficulty)
    let bestTime = props.time
    let newRecord = false
    const [newGame,setNewGame] = useState(false)

    data.once("value",(snapshot) => {
        snapshot.forEach((childSnapshot) => {
            if (childSnapshot.key() == props.difficulty && childSnapshot.val() < bestTime)
                bestTime = childSnapshot.val()
        })
    })
    if (bestTime == props.time) newRecord = true
    

    if (newGame)
        return(
            <IntroPage/>
        )
    else
        return(
            <div id= "background_game_over">
                <div className= "section">
                    <p style= {{fontSize: '6vmin'}}>Nice Work!</p>
                    <p style= {{fontSize: '4vmin'}}>Time: {props.time}</p>
                    {newRecord ? <p>New Record!!</p>:<p>Best Time: {bestTime}</p>}
                </div>
                <button className = "new_game" onClick= {() => {setNewGame(true)}}>New Game</button>
            </div>
        )
}

export default GameOver
