        switch (event.keyCode) {
            case 37:
                this.setState({ ballDirection: 'left' })
                break
            case 39:
                this.setState({ ballDirection: 'right' })
                break
            default:
        }
        this.setState({
            keyPressed: true
        })
      }

    //runs when we update props or components
    render() {
        // If the run was a success
        if (this.state.isGameOver) {
            return (
                <GameOver
                    //define components
                />
            )
        }

        return(<div>
            <div id = "fill_screen" style= {{left: this.state.xPos}} >
                
            </div><div><img src= "https://clipart.info/images/ccovers/1495749720Bowling-Ball-PNG-Clip-Art.png"
                    style= {{height: this.state.ballSize, 
                        top: this.state.yPos}} 
                    id= "ball-image"
                    className = 'Ball'
                    alt = "ball"/>    </div></div>
        )
    }
}
export default LineRiderGame;
