import React from 'react'
import './GameOver.css'
import database from '../firebase'

function GameOver(props) {
    let bestTime = 0
    let count = 0
    database.ref('/users/'+props.difficulty).push(props.time)
    database.ref('/users/'+props.difficulty).orderByValue()
    database.ref('users'+props.difficulty).on("value", (snapshot) => {
        snapshot.forEach( (child) => {
            if (count == 0) {
                bestTime = child.val()
                alert(bestTime)
                count++
            }
            else database.ref('users'+props.difficulty+'/'+child.key).remove()
        })
    })

    let newRecord = false

    if(bestTime == props.time) newRecord = true;
    
    else
        return(
            <div id= "background_game_over">
                <div className= "section">
                    <p style= {{fontSize: '6vmin'}}>Nice Work!</p>
                    <p style= {{fontSize: '4vmin'}}>Time: {props.time}</p>
                    {newRecord ? <p>New Record!!</p>:<p>Best Time: {bestTime}</p>}
                </div>
                <button className = "new_game" onClick= {() => {document.location.reload()}}>New Game</button>
            </div>
        )
}

export default GameOver
